    public static char[][] updateBoard(char[][] board, int[] click) {

        int x = click[0];
        int y = click[1];

        if(board[x][y] == 'M'){
            board[x][y] = 'X';
            return board;
        }

        dfs(board, x, y);
        return board;

    }

    static void dfs(char[][] board, int x_, int y_){
        //递归终止条件， 当碰到四周有 雷的格子，要返回

        //遍历当前格子
        int count = 0;
        for(int i = 0; i < 8; i++){
            int x = x_ + moveX[i];
            int y = y_ + moveY[i];

            if(x < 0 || x >= board.length || y < 0 || y >= board[0].length) continue;

            if(board[x][y] == 'M' || board[x][y] == 'X'){
                count++;
            }
        }

        if(count > 0){
            board[x_][y_] = (char)(count + '0');
            return;
        }

        //能走到这里，说明格子周围没有雷，说明是B
        board[x_][y_] = 'B';
        for(int i = 0; i < 8; i++){
            int x = x_ + moveX[i];
            int y = y_ + moveY[i];
            //寻找周围还没有翻过的格子E
            if(x < 0 || x >= board.length || y < 0 || y >= board[0].length || board[x][y] != 'E') continue;
            dfs(board, x, y);
        }
    }




/////////////////////bfs

    public static char[][] updateBoard2(char[][] board, int[] click) {
        int x = click[0];
        int y = click[1];

        if(board[x][y] == 'M'){
            board[x][y] = 'X';
            return board;
        }
        boolean[][] visited  = new boolean[board.length][board[0].length];
        bfs(board, visited, x, y);
        return board;
    }

    static void bfs(char[][] board,boolean[][] visited, int x, int y){

        Deque<int[]> queue = new ArrayDeque<>();

        queue.offer(new int[]{x, y});
        visited[x][y] = true;

        while(!queue.isEmpty()){
            int[] index = queue.poll();

            int count = 0;
            for(int i = 0; i < 8; i++){
                int newX = index[0] + moveX[i];
                int newY = index[1] + moveY[i];
                if(newX < 0 || newX >= board.length || newY < 0 || newY >= board[0].length) continue;
                if(board[newX][newY] == 'M' || board[newX][newY] == 'X') count++;
            }

            //这一步是理解扫雷的关键，就是向四周扩展时 有个边界， 边界的判断就时 如果出现了数字，就不应该再将其 四周的违反开格子丢进队列， dfs中也有体现，出现了数字，递归路径结束（递归终止条件）
            if(count > 0){
                board[index[0]][index[1]] = (char)(count + '0');
            }else{
                board[index[0]][index[1]] = 'B';

                for(int i = 0; i < 8; i++){
                    int newX = index[0] + moveX[i];
                    int newY = index[1] + moveY[i];
                    if(newX < 0 || newX >= board.length || newY < 0 || newY >= board[0].length) continue;
                    if(board[newX][newY] == 'E' && visited[newX][newY] == false){
                        queue.offer(new int[]{newX, newY});
                        visited[newX][newY] = true;
                    }
                }
            }

        }
    }




